import React, {useState} from 'react';
import ReactDOM from 'react-dom';
var App=(props)=>{
  var [state,setState]=useState({count:props.count, text: ''});

  return(
    <div>
      <h2>The current{state.text||'count'}is {state.count}</h2>
      <button onClick={()=>setState({count:state.count+1})}>+1</button>
      <button onClick={()=>setState({count:state.count-1})}>-1</button>
      <button onClick={()=>setState({count:props.count})}>reset</button>
    <input value={state.text}
    onChange={(evt)=>setState({text:evt.target.value})}/>
    </div>
  );
};
App.defaultProps={
  count:0,
};

ReactDOM.render( <App />,document.getElementById('root'));
